sys_script_include.script:
var getCfg = Class.create();
getCfg.prototype = {
	initialize: function (oConfig, sLogName) {
		this.sLogPrefix = 'getCfg - ';
		this._gt = new global.GlobalTools();
		this._gtObject = new global.GlobalToolsObject();
		if (!oConfig) {
			this.oConfig = {};
			return;
		} // if not ocfg
		var sType = this._gt.getType(oConfig);
		var bIsGlideRecord = (sType.indexOf('gliderecord') == -1) ? false : true;
		if (sLogName) {
			gs.info(this.sLogPrefix + 'initialize sLogName: ' + sLogName + ' sType: ' + sType + ' bIsGlideRecord: ' + bIsGlideRecord);
		} // if log
		this._CfgMgr = new ConfigurationManager();
		if (bIsGlideRecord) {
			this.oConfig = this._CfgMgr.getConfig(oConfig);
		} else if (sType == 'object') {
			this.oConfig = oConfig;
		} else if (sType == 'string') {
			this.oConfig = this._CfgMgr.getConfigByName(oConfig);
			if (!this.oConfig) {
				this.oConfig = this._CfgMgr.getConfigById(oConfig);
			} // if not found by name
		} // if record, object or string
		if (!this.oConfig) {
			this.oConfig = {};
		} // if not valid config
	}, // function initialize

	getConfig: function () {
		return this.oConfig;
	}, // function getConfig

	getValue: function (sConfigPath, sReplace, oConfig) {
		if (!oConfig) {
			oConfig = this.oConfig;
		} // if no object
		return this._gtObject.getValue(sConfigPath, sReplace, oConfig);
	}, // function getConfigValue

	type: 'getCfg',
	version: '1.1.0',
	author: 'Tom Hauri',
	email: 'tom@hauri.biz'

};
